mapper.update:
update user_info
set user_zan=user_zan+1
where user_id=#{uid}

update user_info
 set user_zan=user_zan-1
 where user_id=#{uid}

update user_info
set user_point=user_point+#{extra}
where where user_id=#{uid}

update user_info
set user_balance=user_balance+#{extra}
where where user_id=#{uid}
